mapper.select:
		select * from user u
		join branch b on b.branch_id = u.branch_id
        where u.branch_id = #{branch_id}

mapper.update:
UPDATE user 
SET branch_id = (
    SELECT branch_id 
    FROM (SELECT branch_id FROM user WHERE user_id = #{initial_id}) AS subquery
) 
WHERE user_id = #{user_id};

